function isMobile() { const ua = navigator.userAgent; if (/(tablet|ipad|playbook|silk)|(android(?!.*mobi))/i.test(ua)) { return true } if (/Mobile|Android|iP(hone|od)|IEMobile|BlackBerry|Kindle|Silk-Accelerated|(hpw|web)OS|Opera M(obi|ini)/.test(ua)) { return true } if (window.screen.availWidth < 1024) { return true } return false } function moveNavigation() { var navigation = document.getElementsByClassName('cd-main-nav-wrapper')[0]; var isDeviceMobile = isMobile(); if (!isDeviceMobile) { navigation.parentNode.insertBefore(navigation, document.querySelector('a.cd-nav-trigger')) } else { document.getElementById("main").insertBefore(navigation, document.querySelector('div.cd-main-content').nextElementSibling) } } function initNav() { moveNavigation(); window.addEventListener("resize", function () { (!window.requestAnimationFrame) ? setTimeout(moveNavigation, 300) : window.requestAnimationFrame(moveNavigation) }); document.querySelector("div.cd-logo").addEventListener("click", function (e) { e.preventDefault(); let mainDiv = document.querySelector("div#main"); mainDiv.scrollIntoView({ behavior: 'smooth' }) }); var myAnchors = document.querySelectorAll("a.menu-link"); for (var i = 0; i < myAnchors.length; i += 1) { myAnchors[i].addEventListener("click", function (event) { event.preventDefault(); document.querySelector(this.getAttribute("href")).scrollIntoView({ behavior: 'smooth' }) }, false) } Array.prototype.forEach.call(document.getElementsByClassName("cd-nav-trigger"), (elem) => { elem.addEventListener("click", function (e) { e.preventDefault(); Array.prototype.forEach.call(document.querySelectorAll("header.header-menu, .cd-main-content, footer, .cd-main-nav"), function (elem, index, list) { elem.classList.toggle("nav-is-visible") }) }) }) }